package com.example.varda.poolshop;

import android.content.Context;
import android.content.Intent;
import android.support.annotation.NonNull;
import android.support.v7.widget.RecyclerView;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.ImageView;
import android.widget.TextView;

import com.bumptech.glide.Glide;
import com.bumptech.glide.load.engine.DiskCacheStrategy;

import java.util.ArrayList;
import java.util.Random;

public class RequestsAdapter extends RecyclerView.Adapter<RequestsAdapter.ViewHolder> {

    private ArrayList<RequestDeliveryModel> data;
    RequestActivity r2;
    //private AppointmentClickListener appCompatActivity;

    public RequestsAdapter(ArrayList<RequestDeliveryModel> data, RequestActivity r) {
        this.data = data;
        this.r2 = r;
    }

    @NonNull
    @Override
    public ViewHolder onCreateViewHolder(@NonNull ViewGroup viewGroup, int i) {

        Context context = viewGroup.getContext();
        LayoutInflater inflater = LayoutInflater.from(context);
        View contactView = inflater.inflate(R.layout.driver_card_view, viewGroup, false);
        ViewHolder viewHolder = new ViewHolder(contactView);
        return viewHolder;
    }

    @Override
    public void onBindViewHolder(@NonNull final ViewHolder viewHolder, int i) {
        final RequestDeliveryModel element = data.get(i);
        //https://media.licdn.com/dms/image/C5603AQG9B8kLzr5NCg/profile-displayphoto-shrink_800_800/0?e=1560988800&v=beta&t=3n_Cuvav-61Dtt-TzwRox8oXMT8ph60vrGmLySmF1Xs


        viewHolder.date.setText(element.date);
        viewHolder.time.setText(element.time);
        viewHolder.appLocationPick.setText(element.location);
        if(element.name.equals("Saumya")) {
            viewHolder.name.setText("Saumya Sinha");
            Glide.with(r2.getApplicationContext()).load("https://media.licdn.com/dms/image/C5603AQGyzGrWu1ekbw/profile-displayphoto-shrink_800_800/0?e=1560988800&v=beta&t=SahHNvNwJyRdoUunXKz7dR5TUuDr2ktBgKt7Y388Ulg")
                    .centerCrop()
                    .crossFade()
                    .override(150, 150)
                    .thumbnail(0.5f)
                    .bitmapTransform(new CircleTransform(r2.getApplicationContext()))
                    .diskCacheStrategy(DiskCacheStrategy.ALL)
                    .into(viewHolder.imageView);

        }

        if(element.name.equals("Pragyan")) {
            viewHolder.name.setText("Pragyan Sharma");
            Glide.with(r2.getApplicationContext()).load("https://media.licdn.com/dms/image/C5603AQG9B8kLzr5NCg/profile-displayphoto-shrink_800_800/0?e=1560988800&v=beta&t=3n_Cuvav-61Dtt-TzwRox8oXMT8ph60vrGmLySmF1Xs")
                    .centerCrop()
                    .crossFade()
                    .override(150, 150)
                    .thumbnail(0.5f)
                    .bitmapTransform(new CircleTransform(r2.getApplicationContext()))
                    .diskCacheStrategy(DiskCacheStrategy.ALL)
                    .into(viewHolder.imageView);

        }

        if(element.name.equals("Vardan")) {
            viewHolder.name.setText("Vardan Gupta");
            Glide.with(r2.getApplicationContext()).load("data:image/png;base64,%E2%80%A6")
                    .centerCrop()
                    .crossFade()
                    .override(150, 150)
                    .thumbnail(0.5f)
                    .bitmapTransform(new CircleTransform(r2.getApplicationContext()))
                    .diskCacheStrategy(DiskCacheStrategy.ALL)
                    .into(viewHolder.imageView);

        }

        Random r = new Random();
        int p =  r.nextInt((15 - 5) + 1) + 5;
        viewHolder.day.setText("$" + String.valueOf(2));
        viewHolder.map.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View v) {
                Intent intent = new Intent(r2, AcceptPickupRequest.class);
                r2.startActivity(intent);
            }
        });
    }

    @Override
    public int getItemCount() {
        return data.size();
    }

    public class ViewHolder extends RecyclerView.ViewHolder {

        TextView name;
        TextView time;
        TextView date;
        TextView day;
        ImageView imageView;
        TextView delete;
        TextView map;
        TextView appLocationPick;
        TextView descriptionButton;

        public ViewHolder(@NonNull View itemView) {
            super(itemView);
            map = (TextView) itemView.findViewById(R.id.map);
            name = (TextView) itemView.findViewById(R.id.profName);
            time = (TextView) itemView.findViewById(R.id.appTime);
            date = (TextView) itemView.findViewById(R.id.appDate);
            day = itemView.findViewById(R.id.appDay);
            appLocationPick = itemView.findViewById(R.id.appLocationPick);
            imageView = (ImageView) itemView.findViewById(R.id.imageView);
            delete = (TextView) itemView.findViewById(R.id.deleteButton);
            //descriptionButton = (TextView) itemView.findViewById(R.id.descButton);
        }

    }
}
